import { useState } from "react";
import { plantList } from "./datas/plantList";
import PlantItem from "./PlantItem";
import Cart from "./Cart";

function ShoppingList(){
    const [cartItems, setCartItems] = useState([]);
    const addToCart = (name, price) => {
        setCartItems([...cartItems, {name, price}]);
    }
    const uniqueCategories = [
        ...new Set(plantList.map(plant => plant.category))
    ];

    console.log(uniqueCategories);
    console.log(plantList);

    return(
        <div className="ShoppingList">
            <div className="category">
                <select className="selectCategory">
                    <option value=''>---</option>
                    {uniqueCategories.map((category, index) => (
                        <option key={index} value={category} className="categories">{category}</option>
                    ))}
                </select>
                <button>r√©initialiser</button>
            </div>
            <ul className="ulPlant">
                {plantList.map(({id, image, name, price, water, light}) => (
                    <PlantItem
                        id={id}
                        image={image}
                        name={name}
                        price={price}
                        water={water}
                        light={light}
                        addToCart={addToCart}
                    />
                ))} 
            </ul>
        </div>

    )
}

export default ShoppingList
