import { plantList } from "./datas/plantList";

function ShoppingList(){
    const uniqueCategories = [
        ...new Set(plantList.map(plant => plant.category))
    ];

    console.log(uniqueCategories);
    console.log(plantList);

    return(
        <div className="ShoppingList">
            <select className="selectCategory">
                <option value=''>---</option>
                {uniqueCategories.map((category, index) => (
                    <option key={index} value={plant.category} className="category">{category}</option>
                ))}
            </select>
            <ul className="ulPlant">
                {plantList.map((plant) => (
                    <li key={plant.id} className="plant">
                        <img src={plant.image} alt={plant.name} className="imagePlant"/>
                        <div className="price">{plant.price}</div>
                        {plant.name}
                        {plant.isBestSale && <span>ðŸ”¥</span>}
                        {plant.isSpecialOffer && <span className="solde"> Solde!</span>}
                    </li>     
                ))} 
            </ul>
        </div>

    )
}

export default ShoppingList
