import CareScale from "./CareScale";
import Cart from "./Cart";

function handleClick(plantPrice, plantName) {
    let cart = (`${plantName}: ${plantPrice}€`);
    console.log(cart);
    totalPrice += plantPrice;
    console.log(totalPrice);
}

function PlantItem({id, image, name, price, water, light}){
    return (
        <li key={id} className="plant">
            <img src={image} alt={`${name} image`} className="imagePlant"/>
            <div className="price">{price}€</div>
                {name}
                <CareScale careType='water' scaleValue={water}/>
                <CareScale careType='light' scaleValue={light}/>
            <button onClick={() => Cart(price, name)}>Ajouter</button>
        </li>
    )  
}

export default PlantItem
