import { plantList } from "./datas/plantList";
import PlantItem from "./PlantItem";

function Cart({name, price}){
    const title = 'Panier';
    let totalPrice = 0;
    let cart = (`${name}: ${price}€`);

    if(name === null && price === null){
        return (<p>Votre panier est vide.</p>)

    }
    else{
        return(
            <div className="cart">
                <h2 className="title-cart">{title.toUpperCase()}</h2>
                <ul className="ul-cart">
                    <li>{cart}</li>
                </ul>
                <h3 className="total-cart">Total price: {totalPrice += price}€</h3>
            </div>
        )           
    }       
}

export default Cart
