import { plantList } from "./datas/plantList";

function ShoppingList(){
    const uniqueCategories = [
        ...new Set(plantList.map(plant => plant.category))
    ];

    console.log(uniqueCategories);
    console.log(plantList);

    return(
        <div className="ShoppingList">
            <p className="title">Voici nos plantes :)</p>
            <ul>
                {uniqueCategories.map((category, index) => (
                    <li key={index} className="category">. {category}</li>
                ))}
            </ul>
            <ul>
                {plantList.map((plant) => (
                    <li key={plant.id} className="plant">- {plant.name} {plant.isBestSale ? <span>bestSale</span> : null}</li>
                ))} 
            </ul>
        </div>

    )
}

export default ShoppingList
